import {
  Alert,
  AlertDescription,
  AlertIcon,
  AlertTitle,
} from "@chakra-ui/react";
import { Button } from "@chakra-ui/button";
import { Container } from "@chakra-ui/react";
import { Spinner } from "@chakra-ui/react";

function ErrorFallback({ error, resetErrorBoundary }) {
  return (
    <Container maxW="xl" centerContent margin="auto">
      <Alert
        status="info"
        variant="subtle"
        flexDirection="column"
        alignItems="center"
        justifyContent="center"
        textAlign="center"
        height="390px"
        //bg="purple.800"
        bg="grey"
        //color="yellow.300"
        color=""
        borderRadius="16px"
        borderWidth="2px"
        borderColor="black"
      >
        <AlertIcon boxSize="45px" mr={0} color="white" />
        <AlertTitle mt={5} fontSize="3xl">
          Your chats are Loading ⌛
        </AlertTitle>
        <Spinner
          m={6}
          thickness="6px"
          speed="0.99s"
          emptyColor="gray.200"
          color="yellow.400"
          size="xl"
        />
        <AlertDescription  maxWidth="md" color="white">
          ( Still ⌛ Click on the Button Below ⬇️ )
        </AlertDescription>
        <Button
          fontWeight="bold"  
          variant="ghost"
          bg="yellow.300"
          mt={7}
          color="black"
          borderColor="black"
          onClick={resetErrorBoundary}
          _hover={{ background: "pink.500", color: "white" }}
          _active={{ background: "pink.500", color: "white" }}
        >
          Continue Texting ➡️
        </Button>
      </Alert>
    </Container>
  );
}

export default ErrorFallback;
